mapper.select:
select *, ROUND (@num:=@num+1) as num
from (select @num:=0) a,post
limit #{startPage}, #{endPage}

mapper.update:
update post
set title=#{title}, content=#{content}, description=#{description}, thumbnail=#{thumbnail}, updated=curdate()
where pid=#{pid}

update post
set likes=likes+1
where pid=#{pid}
